$color-fondo-app: #87ceeb;
$color-bordes: #ebb06e;
$color-botones: #b1eb7a;
$color-extra-1: #6ec7eb;
$color-extra-2: #e29deb;
$max-width: 373px;
$max-height: 808px;
$splash-title-width: 332px;
$splash-title-bg-color: rgba(0, 0, 0, 0.5);

* {
    margin: 0;
    padding: 0;
    font-family: sans-serif;
    width: $max-width;
}

#header {
    text-align: center;
    justify-content: center;
    padding-top: 20px;
    padding-bottom: 20px;
    position: fixed;
    margin: 0;
    z-index: 999;

    a {
        text-decoration: none;
        width: $max-width;
        color: black;
        font-weight: 600;
        text-align: center;
        width: $splash-title-width;
        font-size: 45px;
        margin: 0;
    }

    --s: 29px;
    /* control the size */
    --b: 3;
    /* control the thickness*/

    --g:#0000 calc(100% - var(--s)/var(--b) - 1px),
    #b1eb7a
    /* 1st color*/
    calc(100% - var(--s)/var(--b)) calc(100% - 1px),
    #0000;
    --r:calc(2.414*var(--s));
    --p:calc(1.414*var(--s));
    background: radial-gradient(var(--r) at 0 0, var(--g)) var(--r) var(--r),
    radial-gradient(var(--s) at 0 0, var(--g)) var(--p) var(--p),
    radial-gradient(var(--r) at 0 100%, var(--g)) 0 var(--p),
    radial-gradient(var(--s) at 0 100%, var(--g)) calc(-1*var(--s)) var(--r),
    radial-gradient(var(--r) at 100% 0, var(--g)) var(--p) 0,
    radial-gradient(var(--s) at 100% 0, var(--g)) var(--r) calc(-1*var(--s)),
    radial-gradient(var(--r) at 100% 100%, var(--g)) calc(-1*var(--s)) calc(-1*var(--s)),
    radial-gradient(var(--s) at 100% 100%, var(--g)) #ebb06e
    /* 2nd color */
    ;
    background-size: calc(2*var(--r)) calc(2*var(--r));

}

.btn {
    width: 80%;
    padding: 20px;
    margin-bottom: 50px;
    border-radius: 8px;
    border: 1px $color-bordes solid;
    background-color: $color-botones;
    cursor: pointer;
    font-size: 20px;
    text-align: center;

    &:hover {
        background-color: $color-bordes;
    }
}

section {
    --s: 37px;
    /* control the size */

    --c: #0000, #87ceeb .5deg 119.5deg, #0000 120deg;
    --g1: conic-gradient(from 60deg at 56.25% calc(425%/6), var(--c));
    --g2: conic-gradient(from 180deg at 43.75% calc(425%/6), var(--c));
    --g3: conic-gradient(from -60deg at 50% calc(175%/12), var(--c));
    background:
        var(--g1), var(--g1) var(--s) calc(1.73*var(--s)),
        var(--g2), var(--g2) var(--s) calc(1.73*var(--s)),
        var(--g3) var(--s) 0, var(--g3) 0 calc(1.73*var(--s)) $color-extra-1;
    background-size: calc(2*var(--s)) calc(3.46*var(--s));
    // background-color: $color-fondo-app;
}

#title-section {
    background-image: url("../assets/splash-bg.jpg");
    height: 808px;
    justify-content: center;
    align-items: center;
    background-size: cover;
    background-position: center;
    color: white;

    h1 {
        text-align: center;
        width: $splash-title-width;
        font-size: 45px;
        margin: 0;
        padding: 20px;
        background-color: $splash-title-bg-color;
        position: relative;
        top: 50%;
    }
}

#start-section {
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: $max-height;

    button {

        &:first-child,
        &:last-child {
            color: black;
            padding-left: 30px;
            background-repeat: no-repeat;
            background-position: 10px center;
        }
    }

    button {
        position: relative;
        top: 35%;
        left: 9%;
    }
}


#dream-list-section {
    height: $max-height;
    flex-direction: column;
    align-items: center;

    .section-title {
        text-align: center;
        justify-content: center;
        font-size: 30px;
        color: black;
        padding-top: 120px;
        padding-bottom: 20px;

    }

    .list-container {
        max-height: 450px;
        margin-left: 30px;
        overflow-y: auto;
        overflow-x: hidden;
        border: 1px solid #ccc;
        margin-bottom: 10px;
        width: 310px;

        ul {
            list-style: none;
            padding: 0;
            margin: 0;

            li {
                padding: 10px;
                border-bottom: 3px solid $color-extra-2;
                text-align: center;
                justify-content: center;
                width: 290px;
                list-style: none;
                border-radius: 8px;
                background-color: $color-extra-1;

                &:hover {
                    background-color: $color-extra-2;
                }

                a {
                    color: black;
                    text-decoration: none;
                    font-size: 20px;
                }
            }

        }
    }

    button {
        position: relative;
        bottom: 20px;
        top: 20px;
        margin-left: 30px;
    }
}

#record-dream-section {
    height: $max-height;
    margin-top: 0px;
    flex-direction: column;
    align-items: center;

    .section-title {
        text-align: center;
        justify-content: center;
        font-size: 30px;
        padding-top: 120px;
    }

    form {
        margin-top: 50px;
        margin-left: 30px;
        width: 300px;


        label {
            display: block;
            margin-bottom: 5px;
            font-size: 20px;
            width: 300px;
        }

        input[type="date"],
        input[type="text"],
        textarea {
            width: 100%;
            padding: 8px;
            margin-bottom: 10px;
            border: 1px solid black;
            border-radius: 5px;
            font-size: 14px;
        }

        input[type="date"] {
            display: inline-block;
            margin-right: 10px;
        }

        input[type="text"] {
            display: inline-block;
        }

        textarea {
            height: 150px;
        }

        .btn {
            display: inline-block;
            padding: 20px 20px;
            background-color: $color-botones;
            border: 1px solid $color-bordes;
            border-radius: 5px;
            cursor: pointer;
            margin-right: 10px;
            margin-left: 28px;
            margin-top: 10px;

            &:hover {
                background-color: $color-bordes;
            }
        }
    }
}

#saved-dream-section {
    height: $max-height;
    margin-top: 0px;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    .section-title {
        text-align: center;
        justify-content: center;
        font-size: 30px;
        padding-top: 120px;
    }



    .btn {
        display: inline-block;
        padding: 20px 20px;
        background-color: $color-botones;
        border: 1px solid $color-bordes;
        border-radius: 5px;
        cursor: pointer;
        margin-right: 10px;
        margin-left: 30px;
        margin-top: 90px;

        &:hover {
            background-color: $color-bordes;
        }
    }
}

#dream-details-section {
    height: $max-height;
    flex-direction: column;
    align-items: center;

    .section-title {
        text-align: center;
        justify-content: center;
        // margin-top: 120px;
        font-size: 30px;
        padding-top: 120px;
        padding-bottom: 30px;
    }

    .details-dream-container {
        background-color: $color-fondo-app;
        padding-top: 50px;
        width: 320px;
        height: 300px;
        margin-left: 30px;
        overflow-x: auto;
        overflow-y: auto;

        p {
            padding-top: 10px;
            padding-bottom: 5px;
            max-width: 300px;
            justify-content: center;
            font-size: 20px;
        }
    }

    .btns-container {
        margin-top: 20px;
        max-height: 250px;
        padding-left: 30px;
    }
}

#backToStartBtn {
    margin-top: 20px;
    margin-left: 30px;
}

.hidden {
    display: none;
}
